import React, { useState } from "react";
import Info from './Info';

function Sentence(prop){
    const[onHover, setHover] = useState(false);

    function HoverOver() {
        setHover(true);
    }
    function HoverOff(){
        setHover(false);
    }
    return(
        <div>
            <h2 onClick={HoverOver}>{prop.text}</h2>
            {onHover ? <Info text='definition' onDelete={HoverOff}></Info>  : null}
        </div>
    );
}
export default Sentence;
