import React, { useState } from "react";

var snakeList = [];

function Snake(){

    const board = document.createElement('table');
    const max = 22;
    for(let i = 0; i < max; i++){
        const tr = board.insertRow();
        for(let j = 0; j < max; j++){
            const td = tr.insertCell();
            //td.appendChild(document.createTextNode("___"));
            td.style.border = '1px solid black';
        }
    }
    const start = [Math.floor((max - 1) / 2), Math.floor((max - 1) / 2)];

    var berry = [10, 10];
    //board.rows.item(berry[0]).cells.item(berry[1]).innerHTML = "***";

    const len = 1;

    const direction = [0, 0];
    const startTimer = true;

    for(let i = 0; i < len; i++){
        snakeList.push([start[0] + i, start[1]]);
    }

    var eaten = false;
    var update = true;
    var begin = true;

    var timerSpeed = 100;

    document.addEventListener('keydown', function(event){

        begin = false;
        //board.rows.item(start[0]).cells.item(start[1]).innerHTML = "___";
        //left arrow
        if(event.keyCode == 37 && update){
            //if(start[1] > 0){
                //start[1]--;
           //}
           if(direction[1] != 1){
                direction[1] = -1;
                direction[0] = 0;
                update = false;
           }

        }

        //down arrow
        else if(event.keyCode == 40 && update){
            //if(start[0] < max - 1){
                //start[0]++;
            //}
            if(direction[0] != -1){
                direction[0] = 1;
                direction[1] = 0;
                update = false;
            }

        }

        //right arrow
        else if(event.keyCode == 39 && update){
            //if(start[1] < max - 1){
                //start[1]++;
           // }
           if(direction[1] != -1){
                direction[1] = 1;
                direction[0] = 0;
                update = false;
           }
        }

        //up arrow
        else if(event.keyCode ==38 && update){
            //if(start[0] > 0){
                //start[0]--;
            //}
            if(direction[0] != 1){
                direction[0] = -1;
                direction[1] = 0;
                update = false;
            }
        }

        if(startTimer){

        }  
        startTimer = false;

    });

    const lab = document.createElement("label");
    lab.innerHTML = eaten;

    var timer = setInterval(function(){
        if(start[0] < 0 || start[0] > max - 1 || start[1] < 0 || start[1] > max - 1){
            //clearInterval(timer);
            //document.body.innerHTML = "";
            end(timer);
        }

        //board.rows.item(berry[0]).cells.item(berry[1]).innerHTML = "***";
        board.rows.item(berry[0]).cells.item(berry[1]).style.backgroundColor = "red";

        //updates position of snake
        start[0] += direction[0];
        start[1] += direction[1];
        //board.rows.item(start[0]).cells.item(start[1]).innerHTML = "***";
        board.rows.item(start[0]).cells.item(start[1]).style.backgroundColor = "green";


        snakeList.unshift([start[0], start[1]]);

        if(start[0] == berry[0] && start[1] == berry[1]){
            eaten = true;
            berry[0] = Math.floor(Math.random() * max);
            berry[1] = Math.floor(Math.random() * max);
            timerSpedd += 100;
        }

        if(!eaten){
            //board.rows.item(snakeList[snakeList.length - 1][0]).cells.item(snakeList[snakeList.length - 1][1]).innerHTML = "___";
            board.rows.item(snakeList[snakeList.length - 1][0]).cells.item(snakeList[snakeList.length - 1][1]).style.backgroundColor = "white";
            snakeList.splice(snakeList.length - 1, 1);
        }
        else{
            eaten = false;
        }

        update = true;
        //lab.innerHTML = start + " \\\\\\\ " + snakeList;

        if(!begin){
            for(let i = 1; i < snakeList.length; i++){
                if(start[0] == snakeList[i][0] && start[1] == snakeList[i][1]){
                    //clearInterval(timer);
                    //document.body.innerHTML = "";
                    end(timer);
                }
            }
        }
    }, timerSpeed);


    document.body.appendChild(board);
    document.body.appendChild(lab);

}

