    clearInterval(timer);
    document.body.innerHTML = "";

    const retry = document.createElement("button");
    retry.innerHTML = "RETRY";
    retry.className = "retry";
    retry.addEventListener("click", function(){
        Snake();
    })
    document.body.appendChild(retry);
