import React, { useState } from "react"
import Card from "./card";

function FlashCard(prop){
    const[onEdit, setEdit] = useState(false);

    function edit() {
        setEdit(true);
    }
    function unEdit(){
        setEdit(false);
    }

    const[flipped, setFlip] = useState(false);

    function flipFront() {
        setEdit(!flipped);
    }
    function flipBack(){
        setEdit(false);
    }




    return(
        <div>
        <label for='front'>front   </label>
        <textarea id='front'></textarea>

        <button id="save" onClick={edit}>Save</button>
        <button onClick={unEdit}>Edit</button>

        <br></br>

        <label for='bask'>back   </label>
        <textarea id='back'></textarea>

        {onEdit ? <div className='flashcard'>
            <Card fr={document.getElementById("front").value}
        ba={document.getElementById("back").value}></Card>
        <button onClick={unEdit}>FLIP</button>
            </div> : 
        null}

        <div className='cards'></div>
        </div>
    );
}
export default FlashCard;
