import FlashCard from './FlashCard';
import React, { useState } from "react";

function App() {
  const[create, setCreate] = useState(false);

    function createCard() {
        setCreate(true);
    }
    function deleteCard(){
        setCreate(false);
    }

    const cardList = [5];
    function addToList(added){
      return function(){
      cardList[0] = added;
      var div = document.createElement("div");
      div.innerHTML = cardList[0];
      document.body.appendChild(div);
      }
    }

    $( '#someButton' ).on( 'click', function() {
      var txtValue = $( '#ta' ).val();
      alert( txtValue );
    });


  return (
      <div>
        <h1>JAPANESE LEARNING</h1>
        <Sentence text='my first sentence'></Sentence>
        <button onClick={createCard}>Add Flashcard</button>
        {create ? <FlashCard text='sentence'></FlashCard>  : null}

        <button onClick={addToList('high')}>add</button>

        <textarea id='bam'>ddd</textarea>
        <button id="someButton">Send info</button>
      </div>

  );
}

export default App;
