import Sentence from './Sentence';
import FlashCard from './FlashCard';
import React, { useState } from "react";

function App() {
  const[create, setCreate] = useState(false);

    function createCard() {
        setCreate(true);
    }
    function deleteCard(){
        setCreate(false);
    }

    var div = document.createElement("div");
    var area = document.createElement("textarea");
    div.appendChild(area);
    var button = document.createElement("button");
    button.addEventListener("click", function(){
      var h2 = document.createElement("h2");
      h2.innerHTML = area.value;
      div.appendChild(h2);

    });
    document.body.appendChild(div);

    const cardList = [5];
    function addToList(added){
      return function(){
      cardList[0] = added;
      var div = document.createElement("div");
      div.innerHTML = cardList[0];
      document.body.appendChild(div);
      }
    }


  return (
      <div>
        <h1>JAPANESE LEARNING</h1>
        <Sentence text='my first sentence'></Sentence>
        <button onClick={createCard}>Add Flashcard</button>
        {create ? <FlashCard text='sentence'></FlashCard>  : null}

        <button onClick={addToList('high')}>add</button>

      </div>

  );
}

export default App;
