import React, { useState } from "react";

function FlashCard(prop){
    const[onEdit, setEdit] = useState(false);

    function edit() {
        setEdit(true);
    }
    function unEdit(){
        setEdit(false);
    }

    return(
        <div>{prop.text}
        <button>Flip</button>
        <button onClick={edit}>Edit</button>
        {onEdit ? document.createElement('input'): null}
        </div>
    );
}
export default FlashCard;
