function Card(prop){
    var frontVal = prop.fr;
    var backVal = prop.ba;
    var currentVal = true;

    function flipCard(){
        currentVal = false;
    }
    return(
        <div className='flashcard'>
            <button onClick={flipCard}>FLIP</button>
            {currentVal ? <h2>{frontVal}</h2> : <h2>{backVal}</h2>}
        </div>
    );
}
export default Card;
