const front = document.getElementById("front");
const back = document.getElementById("back");

let cards = localStorage.getItem('items') ?
JSON.parse(localStorage.getitem('items')) : [];

function saveCard(){
    var cardInfo = {
        'thisFront' : front.value,
        'thisBack' : back.value
    }
    cards.push(cardInfo);
    localStorage.setItem('items', JSON.stringify(cards));

    //divMaker(cards[cards.length - 1]);
    front.value = '';
    back.value = '';
}

