import React, { useState } from "react"
import Card from "./card";

function FlashCard(prop){
    const[onEdit, setEdit] = useState(false);

    function edit() {
        setEdit(true);
    }
    function unEdit(){
        setEdit(false);
    }

    var flipped = null;

    return(
        <div>
        <label for='front'>front   </label>
        <textarea id='front'></textarea>

        <button id="save" onClick={edit}>Save</button>
        <button onClick={unEdit}>Edit</button>

        <br></br>

        <label for='bask'>back   </label>
        <textarea id='back'></textarea>

        {onEdit ? <Card fr={document.getElementById("front").value}
        ba={document.getElementById("back").value}></Card> : 
        null}

        <div className='cards'></div>
        </div>
    );
}
export default FlashCard;
