import Sentence from './Sentence';
import FlashCard from './FlashCard';
import React, { useState } from "react";

var cardList = [];
const deckNames = [];
const cardListArray = [];


function App() {

    const title = document.createElement("h1");
    title.className = 'title';
    title.innerHTML = 'QUIZIFY';
    document.body.appendChild(title);

    const deckWidget = document.createElement("div");
    for(let i = 0; i < cardListArray.length; i++){
      //const deckWidget = document.createElement("div");
      deckWidget.className = 'deck';

      const deckName = document.createElement("h2");
      //deckName.innerHTML = 'tempName';
      deckName.innerHTML = deckNames[i];

      const studyButton = document.createElement("button");
      studyButton.innerHTML = 'STUDY';
      studyButton.className = 'widgetButton';

      studyButton.addEventListener("click", function(){
        document.body.innerHTML = '';

        const returnButton = document.createElement("button");
        returnButton.innerHTML = 'Return';
        returnButton.addEventListener("click", function(){
          document.body.innerHTML = '';
          App();
        });
        document.body.appendChild(returnButton);


        var deckIndex = deckNames.indexOf(deckName.innerHTML);
        let cnt = 0;


          const can = document.createElement("div");
                can.className = "flashcard";

                const frontSide = document.createElement("h2");
                frontSide.innerHTML = cardListArray[deckIndex][cnt][0];
                const backSide = document.createElement("h2");
                backSide.innerHTML = cardListArray[deckIndex][cnt][1];
                can.appendChild(frontSide);
                can.appendChild(backSide)
                backSide.style.display = "none";

                var button = document.createElement("button");
                button.innerHTML = "FLIP";

                button.addEventListener("click", function(){
                    if(frontSide.style.display == "none"){
                        frontSide.style.display = "block";
                        backSide.style.display = "none";
                    }
                    else{
                        frontSide.style.display = "none";
                        backSide.style.display = "block";
                    }

                });
                can.appendChild(button);

                var deleteButton = document.createElement("button");
                deleteButton.innerHTML = 'DELETE';
                deleteButton.className = "deleteButton";
                deleteButton.addEventListener("click", function(){
                  document.body.removeChild(can);
                  for(let i = 0; i < cardListArray[deckIndex].length; i++){
                    if(cardListArray[deckIndex][i][0] == frontSide.innerHTML && cardListArray[deckIndex][i][1] == backSide.innerHTML){
                      cardListArray[deckIndex].splice(i, 1);
                    }
                  }
              });
              can.appendChild(deleteButton);
              document.body.appendChild(can);

              var nextButton = document.createElement("button");
                deleteButton.innerHTML = 'NEXT';
                nextButton.addEventListener("click", function(){
                  document.body.removeChild(can);
                  cnt++
              });

      });

      const deleteWidget = document.createElement("button");
      deleteWidget.innerHTML = "DELETE";
      deleteWidget.className = "deleteButton";;

      deleteWidget.addEventListener("click", function(){
        cardListArray.splice(deckNames.indexOf(deckName.innerHTML), 1);
        deckNames.splice(deckNames.indexOf(deckName.innerHTML), 1);
        document.body.innerHTML = '';
        App();
      });

      deckWidget.appendChild(deckName);
      deckWidget.appendChild(studyButton);
      deckWidget.appendChild(deleteWidget);
      document.body.appendChild(deckWidget);
    }

    //document.body.appendChild(deckWidget);

    const addDeck = document.createElement("button");
    addDeck.innerHTML = "Add New Deck";
    addDeck.addEventListener("click", function(){

      //document.body.removeChild(deckWidget);
      //document.body.removeChild(addDeck);
      //document.body.removeChild(title);
      document.body.innerHTML = '';

      var buttonArray = document.createElement("div");

      var div = document.createElement("div");
      div.className = 'cardCreator';
      var frontText = document.createElement("label");
      frontText.innerHTML = 'FRONT:   ';
      div.appendChild(frontText);

      var front = document.createElement("textarea");
      div.appendChild(front);
      var br = document.createElement("br");
      div.appendChild(br);

      var backText = document.createElement("label");
      backText.innerHTML = 'BACK:   ';
      div.appendChild(br);
      div.appendChild(backText);
      var back = document.createElement("textarea");
      div.appendChild(back);
      div.appendChild(br);


      var createButton = document.createElement("button");
      createButton.innerHTML = 'Create Card';

      createButton.addEventListener("click", function(){
        if(front.value != '' && back.value != ''){
          const newCard = document.createElement("div");

          newCard.className = "flashcard";

                const frontSide = document.createElement("h2");
                frontSide.innerHTML = front.value;
                const backSide = document.createElement("h2");
                backSide.innerHTML = back.value;
                newCard.appendChild(frontSide);
                newCard.appendChild(backSide)
                backSide.style.display = "none";

                var button = document.createElement("button");
                button.innerHTML = "FLIP";

                button.addEventListener("click", function(){
                    if(frontSide.style.display == "none"){
                        frontSide.style.display = "block";
                        backSide.style.display = "none";
                    }
                    else{
                        frontSide.style.display = "none";
                        backSide.style.display = "block";
                    }

                });
                newCard.appendChild(button);

                var deleteButton = document.createElement("button");
                deleteButton.innerHTML = 'DELETE';
                deleteButton.className = "deleteButton";
                deleteButton.addEventListener("click", function(){
                  div.removeChild(newCard);

                  //This deletes a card when you are adding a new deck. It assumes each card is unique
                  for(let i = 0; i < cardList.length; i++){
                    if(cardList[i][0] == frontSide.innerHTML && cardList[i][1] == backSide.innerHTML){
                      cardList.splice(i, 1);
                    }
                  }
                  //cardList.splice(cardList.indexOf(frontSide.innerHTML + "," + backSide.innerHTML), 1);
              });
              newCard.appendChild(deleteButton);
              div.appendChild(newCard);

              cardList.push([front.value, back.value]);
          }

      });
      createButton.addEventListener("click", function(){
        if(front.value != '' && back.value != ''){
          front.value = '';
          back.value = '';
        }
      }); 

      const titleLabel = document.createElement("label");
      titleLabel.innerHTML = 'TITLE: ';

      const titleArea = document.createElement("textarea");

      const saveButton = document.createElement("button");
      saveButton.innerHTML = 'SAVE';
      saveButton.addEventListener("click", function(){
        if(cardList.length != 0 && titleArea.value != ""){  
          //for(let i = 0; i < cardList.length; i++){
            //cardListArray.push([cardList[i][0], cardList[i][1]]);
          //}
          cardListArray.push(cardList);
          cardList = [];
          deckNames.push(titleArea.value);

          document.body.innerHTML = '';
          App();
        }

      });

      const rtnButton = document.createElement("button");
      rtnButton.innerHTML = "RETURN";
      rtnButton.addEventListener("click", function(){
        document.body.innerHTML = '';
        cardList = [];
        App();
      });
      buttonArray.appendChild(rtnButton);
      buttonArray.appendChild(saveButton);

      buttonArray.appendChild(titleLabel);
      buttonArray.appendChild(titleArea);

      div.appendChild(createButton);
      buttonArray.className = 'buttonArray';
      document.body.appendChild(buttonArray);
      document.body.appendChild(div);
    });


    document.body.appendChild(addDeck);



}

export default App;
