                    testArea.innerHTML = wordList[i];
                    tCorrect += 1;
                }
                else{
                    tWrong += 1
                    wrongScore.innerHTML = tWrong;
                }
            }
        }

        if(wordList[i].length == 0){
            wordDone = true;
            i += 1;
            wordCount++;
        }

        if(started){
            var timer = setInterval(function(){
                timerDisplay.innerHTML = '00: ' + sec;
                sec--;

                if(sec < 0 ||  testArea.innerHTML.length == 0/*i == wordList.length*/){
