import React, { useState } from "react"
import Card from "./card";

function FlashCard(prop){
    const[onEdit, setEdit] = useState(false);

    function edit() {
        if(document.getElementById("front").value != ""){
            setEdit(true);
        }
    }
    function unEdit(){
        setEdit(false);
    }

    /*var text = ["text1", "tex2", "text3", "text4"];
    text.forEach(function(el) {
        var div = document.createElement("div");
        div.className = "finalBlock";
        div.innerHTML = el;
        document.body.appendChild(div);
    });*/


    function createCard(front, back){
        /*var newCard = "<h2></h2>";
        var div = document.createElement("div");
        var frontSide = "<h2>" + front + "</h2>";
        var backSide = "<h2>" + back + "</h2>";
        div.className = "flashcard";
        div.innerHTML = frontSide + backSide;
        var newButton = "<button>FLIP</button>";
        div.innerHTML += newButton;
        document.body.appendChild(div);*/
        return (

            <div className='flashcard'>
                <Card fr={front} ba={back}></Card>
            </div>
        );
    }



    const cardList = [5][2];
    var pos = 0;
    function addToCardList(front, back){
        cardList[pos][0] = front;
        cardList[pos][1] = back;
        pos++;
    }


    function createText(front, back){
        return function(){
            var div = document.createElement("div");
            div.className = "flashcard";

            var frontSide = document.createElement("h2");
            frontSide.innerHTML = front;
            var backSide = document.createElement("h2");
            backSide.innerHTML = back;
            div.appendChild(frontSide);
            div.appendChild(backSide)
            backSide.style.display = "none";


            var button = document.createElement("button");
            button.innerHTML = "FLIP";

            button.addEventListener("click", function(){
                if(frontSide.style.display == "none"){
                    frontSide.style.display = "block";
                    backSide.style.display = "none";
                }
                else{
                    frontSide.style.display = "none";
                    backSide.style.display = "block";
                }

            });
            div.appendChild(button);
            document.body.appendChild(div);
        }
    }

    var newCard = <Card fr='front' ba='back'></Card>;
    var div = document.createElement("div");
        var h5 = document.createElement('Card');
        div.className = "flashcard";
        div.id = Card;
        document.body.appendChild(div);

    var frontVal = '';
    var backVal = '';
    function assignVal(front, back){
        return function(){
            frontVal = front;
            backVal = back;
        }
    }


    var div = document.createElement("div");

    var frontText = document.createElement("textarea");
    frontText.id = 'frontText';
    div.appendChild(frontText);

    var button = document.createElement("button");
    button.innerHTML = "TEST";

    button.addEventListener("click", 
        createText('front', 'testBack'));
    document.body.appendChild(div);
    document.body.appendChild(button)




    return(
        <div>
        <label for='front'>front   </label>
        <textarea id='front'></textarea>

        <button id="save" onClick={edit}>Save</button>
        <button onClick={unEdit}>Delete</button>

        <br></br>

        <label for='bask'>back   </label>
        <textarea id='back'></textarea>

        {onEdit ? 
        createCard(document.getElementById("front").value, 
        document.getElementById("back").value): 
        null}


        <button onClick={createText(frontVal, backVal)}>Create</button>



        {createCard('front', 'back')}

        </div>
    );

    /*{onEdit ? 
        <div className='flashcard'>
            <Card fr={document.getElementById("front").value}
            ba={document.getElementById("back").value}></Card>
        </div> : 
        null}*/
}
export default FlashCard;
