import React, { useState } from "react";

const typingTestValue = "sample test text for typing test";
function TypingPage() {

    const title = document.createElement("div");
    title.className = "title";
    title.innerHTML = "Typing Test";

    document.body.appendChild(title);

    const testArea = document.createElement("textarea");
    testArea.className = 'testArea';
    testArea.innerHTML = typingTestValue;
    document.body.appendChild(testArea);


    var tCorrect = 0;
    var tWrong = 0;

    document.body.appendChild(document.createElement('br'));

    const correctScore =document.createElement("label");
    correctScore.innerHTML = tCorrect;
    correctScore.className = "scoreArea";
    document.body.appendChild(correctScore);

    const wrongScore =document.createElement("label");
    wrongScore.innerHTML = tWrong;
    wrongScore.className = "scoreArea";
    document.body.appendChild(wrongScore);

    document.addEventListener('keydown', function(event)
    {
        //var i = 0;
        var tempVal = testArea.innerHTML.substring(0, 1).charCodeAt(0);
        if(tempVal > 90){
            tempVal = tempVal - 32;
        }
        if(event.keyCode == tempVal){
            //typingTestValue = typingTestValue.substring(1, typingTestValue.length - 1);
            testArea.innerHTML = testArea.innerHTML.substring(1, testArea.innerHTML.length);
            //testArea.innerHTML = "ample test text for typing test";
            //i++;
            tCorrect += 1;
            correctScore.innerHTML = tCorrect;
        }
        else{
            tWrong += 1
            wrongScore.innerHTML = tWrong;
        }

        if(testArea.innerHTML.length == 0){
            document.body.removeChild(testArea);
        }

        //if(event.keyCode == 83){
            //testArea.innerHTML = "done";
        //}
    });

}

export default TypingPage;
