import {Card} from "./Card.js";

export class Deck{

    constructor(name){
        this.name = name;
        this.cardList = [];
    }

    getName(){
        return this.name;
    }

    setName(name){
       this.name = name;
    }

    getCardList(){
        return this.cardList;
    }

    addCard(card){
        this.cardList.push(card);
    }

    removeCard(idx){
        if(idx < 0 || idx >= this.cardList.length){
            throw new Error("idx not in cardList");
        }
        this.cardList.splice(idx, 1);
    }

    removeCardName(front, back){
        for(let i = 0; i < this.cardList.length; i++){
            if(this.cardList.getCard(i).getFront == front 
            && this.cardList.getCard(i).getBack == back){
                this.cardList.splice(i, 1);
                break;
            }
        }
    }

    getCard(idx){
        if(idx < 0 || idx >= this.cardList.length){
            throw new Error("idx not in cardList");
        }
        return this.cardList[idx];
    }

    getCardName(front, back){
        for(let i = 0; i < this.cardList.length; i++){
            if(this.cardList.getCard(i).getFront() == front 
            && this.cardList.getCard(i).getBack() == back){
                return this.cardList.getCard(i);
            }
        }
    }
}
