import React, { useState } from "react";

function Snake(){

    const board = document.createElement('table');
    const max = 30
    for(let i = 0; i < max; i++){
        const tr = board.insertRow();
        for(let j = 0; j < max; j++){
            const td = tr.insertCell();
            td.appendChild(document.createTextNode("___"));
            td.style.border = '1px solid black';
        }
    }
    const start = [(max - 1) / 2, (max - 1) / 2];
    board.rows.item(start[0]).cells.item(start[1]).innerHTML = "***";

    const berry = [1, 1];
    board.rows.item(berry[0]).cells.item(berry[1]).innerHTML = "***";

    const len = 2;

    const direction = [0, 0];
    const startTimer = false;

    document.addEventListener('keydown', function(event){
        //board.rows.item(start[0]).cells.item(start[1]).innerHTML = "___";
        board.rows.item(berry[0]).cells.item(berry[1]).innerHTML = "***";

        //left arrow
        if(event.keyCode == 37){
            //if(start[1] > 0){
                //start[1]--;
           //}
           if(direction[1] != 1){
                direction[1] = -1;
                direction[0] = 0;
           }
        }

        //down arrow
        if(event.keyCode == 40){
            //if(start[0] < max - 1){
                //start[0]++;
            //}
            if(direction[0] != -1){
                direction[0] = 1;
                direction[1] = 0;
            }
        }

        //right arrow
        if(event.keyCode == 39){
            //if(start[1] < max - 1){
                //start[1]++;
           // }
           if(direction[1] != -1){
                direction[1] = 1;
                direction[0] = 0;
           }
        }

        //up arrow
        if(event.keyCode ==38){
            //if(start[0] > 0){
                //start[0]--;
            //}
            if(direction[0] != 1){
                direction[0] = -1;
                direction[1] = 0;
            }
        }

        if(start[0] == berry[0] && start[1] == berry[1]){
            len++;
        }
        //start[0] += direction[0];
        //start[1]+= direction[1];
        //board.rows.item(start[0]).cells.item(start[1]).innerHTML = "***";



    });
    var timer = setInterval(function(){
        board.rows.item(berry[0]).cells.item(berry[1]).innerHTML = "***";

        if(start[0] == berry[0] && start[1] == berry[1]){
            len++;
        }

        //updates position of snake
        board.rows.item(start[0]).cells.item(start[1]).innerHTML = "___";
        start[0] += direction[0];
        start[1]+= direction[1];
        for(let i = 0; i < len; i++){
            board.rows.item(start[0] - i).cells.item(start[1] - i).innerHTML = "***";
        }
    }, 100);

    document.body.appendChild(board);

}
export default Snake;
