const front = document.getElementById("front");
const back = document.getElementById("back");

let cards = localStorage.getItem('items') ?
JSON.parse(localStorage.getitem('items')) : [];

function saveCard(){
    var cardInfo = {
        'thisFront' : front.value,
        'thisBack' : back.value
    }
    cards.push(cardInfo);
    localStorage.setItem('items', JSON.stringify(cards));

    divMaker(cards[cards.length - 1]);
    front.value = '';
    back.value = '';
}

function divMaker(text){
    var div = document.createElement("div");
    var h2_front = document.createElement("h2");
    var h2_back = document.createElement("h2");

    div.className = 'card';

    h2_front.setAttribute('style', "border-top:1px; padding: 15px; margin-top: 30px");
    h2_front.innerHTML = text.thisFront;
}
