                for(let j = 1; j < winnerList.length; j++){
                    for(let k = 0; k < winnerList[j][0].length; k++){
                        high2 = Math.max(winnerList[j][0][k][0], pair);
                    }
                    if(high2 > high){
                        high = high2;
                        winner = i;
                    }
                } 
            }
            else{
                winner = i;
                break;
            }
        }
    }
    return winner;
}

export default pokerGame;
