import React, { useState } from "react"
import Card from "./card";
import script from './script';

function FlashCard(prop){
    const[onEdit, setEdit] = useState(false);

    function edit() {
        setEdit(true);
    }
    function unEdit(){
        setEdit(false);
    }

    return(
        <div>
        <label for='front'>front   </label>
        <textarea id='front'></textarea>

        <button id="save" onClick="addCard()">Save</button>
        <button onClick={edit}>Edit</button>

        <br></br>

        <label for='bask'>back   </label>
        <textarea id='back'></textarea>

        {onEdit ? <Card fr={document.getElementById("front").value}
        ba={document.getElementById("back").value}></Card>: null}

        <div className='cards'></div>
        </div>
    );
}

function addCard(){
    var frontValue = document.getElementById("front").value;
    var backValue = document.getElementById("back").value;

    return(
        <h2>{frontValue}</h2>
    );
}
export default FlashCard;
