import React, { useState } from "react"

function Card(prop){
    var frontVal = prop.fr;
    var backVal = prop.ba;
    var currentVal = false;

    const[flipped, setFlip] = useState(false);

    function flip() {
        if(flipped == true){
            setFlip(false);
        }
        else{
            setFlip(true);
        }
    }
    function flipBack(){
        setFlip(false);
    }

    var bem = true
    function deleteThis(){
        bem = false;
    }

    if(bem){
    return(
        <div>
            {flipped ? <h2>{frontVal}</h2> : <h2>{backVal}</h2>}
            <button onClick={flip}>FLIP</button>
            <button onClick={deleteThis}>DELETE</button>
        </div>
    );
    }
    else{
        return null;
    }
}
export default Card;
