import React, { useState } from "react";

const typingTestValue = "sample test text for typing test";
function TypingPage() {

    const title = document.createElement("div");
    title.className = "title";
    title.innerHTML = "Typing Test";

    document.body.appendChild(title);

    const testArea = document.createElement("textarea");
    testArea.className = 'testArea';
    testArea.innerHTML = typingTestValue;
    document.body.appendChild(testArea);

    document.addEventListener('keydown', function(event)
    {
        var i = 0;
        while(i < typingTestValue.length){
            if(event.keyCode == typingTestValue.charAt(i)){
                typingTestValue = typingTestValue.substring(1, typingTestValue.length - 1);
            }
        }
        if(event.keyCode == 83){
            testArea.innerHTML = "done";
        }
    });

}

export default TypingPage;
